
public class FlowEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int score = 0;
		
		score = 70;
		
		if(score >= 60) {
			System.out.println("합격입니다");
			System.out.println("축하합니다"); // 한줄이든 천줄이든 {}블럭을 적어둬야함
		}
		
	    if(score == 60) {
	    	System.out.println("아슬아슬하셨네요"); //if문은 얼마든지 많이 사용가능
	    }
		
		}
	}


