
public class ForFlowEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		while(조건문) {
//			조건식의  연산결과가 참(ture)인 동안, 반복될 문장들을 적는다.
//		}
		
		int num = 0;
		
		num = 1;
//		무한루프(조건식이 참이면 계속 수행되기 때문)
		while(num <= 10) {
			System.out.println(num);
			num++;   
		}
		
	}

}
